'use strict';
var qtools = require('qtools'),
	qtools = new qtools(module),
	events = require('events'),
	util = require('util');

//START OF moduleFunction() ============================================================

var moduleFunction = function(args) {
	events.EventEmitter.call(this);
	this.forceEvent = forceEvent;
	this.args = args;
	this.ping=qtools.ping;

		qtools.validateProperties({
			subject: args,
			targetScope: this, //will add listed items to targetScope
			propList: [
				{
					name: 'finalProcess',
					optional: false
				}
			]
		});

	var self = this,
		forceEvent = function(eventName, outData) {
			this.emit(eventName, {
				eventName: eventName,
				data: outData
			});
		};


	//LOCAL FUNCTIONS ====================================

	//METHODS AND PROPERTIES ====================================



	//INITIALIZATION ====================================

	var sqlizerGenerator = require('sqlizer');

	/*
	Initializes an instance of sqlizer intialized with the tables that have been
	gotten from the data source as callbackList, and hands it to a function
	passed in as finalProcess(), which does pretend SQL to generate
	the correct output.
	*/

	this.execute = function(callbackList) {
		var tables = {}
		var outObj;

		for (var i in callbackList) {
			var element = callbackList[i];
			tables[i] = element;
		}

		this.sqlizer = new sqlizerGenerator({
			tables: tables
		});

		outObj = this.finalProcess(this.sqlizer);
		return outObj;
	}
	return this;
};

//END OF moduleFunction() ============================================================

util.inherits(moduleFunction, events.EventEmitter);
module.exports = moduleFunction;



