'use strict';
var qtools = require('qtools'),
	qtools = new qtools(module),
	events = require('events'),
	util = require('util');

//START OF moduleFunction() ============================================================

var moduleFunction = function(args) {
	events.EventEmitter.call(this);
	this.forceEvent = forceEvent;
	this.args = args;
	this.ping=qtools.ping;

	qtools.validateProperties({
		subject: args,
		targetScope: this, //will add listed items to targetScope
		propList: []
	});

	var self = this,
		forceEvent = function(eventName, outData) {
			this.emit(eventName, {
				eventName: eventName,
				data: outData
			});
		};


	//LOCAL FUNCTIONS ====================================
	


	//METHODS AND PROPERTIES ====================================

self.transferPacket=require("transferPacket");
self.transferPacket=new(self.transferPacket);



this.generateSender = function(res, req) {	

	var pruneOutput = function(result) {
		var outObj = result;
		if (req.query.sendFlatSpecs != 'true') {
			delete outObj.FlatSpecs;
		}
		return outObj;
	}

		return function(err, result) {

			if (!err) {	
				result.result = pruneOutput(result.result);

				self.transferPacket.reset()
					.add('Status', 1)
					.addMerge(result.result);

			}
			else{

			var outObj={
				message:err.result.message,
				evidence:err.result.evidence,
				meta:err.meta
			};

	

				self.transferPacket.reset()
					.add('Error', err.result.message)
					.add('Evidence', err.result.evidence)
					.add('Status', -1);
			
			}
				var meta=err?err.meta:result.meta;
				if (global.localEnvironment.get('sendMetaData')) {
					self.transferPacket.add('Meta', meta)
					.add('Meta', {
						body: req.body,
						query: req.query
					});
				}
				res.json(self.transferPacket.finishedObject());
		}
	};

	//INITIALIZATION ====================================

	

	return this;
};

//END OF moduleFunction() ============================================================

util.inherits(moduleFunction, events.EventEmitter);
module.exports = moduleFunction;